$(function() {
    $.get("/marketingContentPublic/loginPageBrandContent", function(l) {
        for (var k = 0; k < l.length; k++) {
            var m = l[k].imageOverlayAlpha / 100;
            $(".slides").append('<div class="slide' + (k == 0 ? " active" : "") + '" style="background-image:linear-gradient(rgba(0,0,0,' + m + "),rgba(0,0,0," + m + ")),url('" + l[k].imageUrl + '\')">  <div>    <div class="rectangle-top"></div>' + (l[k].url ? '    <a target="_blank" href="' + l[k].url + '">' : "") + (l[k].date ? '      <div class="article-date">' + l[k].date + "</div>" : "") + (l[k].title ? '      <div class="article-title">' + l[k].title + "</div>" : "") + (l[k].subtitle ? '      <div class="article-subtitle">' + l[k].subtitle + "</div>" : "") + (l[k].urlText ? '      <span class="view-post">' + l[k].urlText + "</span>" : "") + (l[k].url ? "    </a>" : "") + '    <div class="rectangle-bottom"></div>  </div></div>')
        }
        h();
        b()
    });
    var f = $(".slider"),
        g = $(".bullets"),
        a = undefined,
        j = 20000;

    function e() { $(".slide.inactive").removeClass("inactiveRight").removeClass("inactiveLeft") }

    function i(k, m, l) {
        k.removeClass("active").addClass("inactive " + l);
        m.removeClass("inactive").addClass("active");
        c();
        setTimeout(e, 300)
    }
    $(document).on("click", ".bullet", function() {
        if ($(this).hasClass("active")) { return }
        var n = $(".slide.active");
        var l = n.index();
        var m = $(this).index();
        var k = $(".slide:nth-child(" + (m + 1) + ")");
        i(n, k, l > m ? "inactiveRight" : "inactiveLeft");
        clearTimeout(a);
        b()
    });

    function h() {
        var n = $(".slide").length,
            k = $(".slide.active").index();
        if (n <= 1) { return }
        for (var m = 0; m < n; m++) {
            var l = $("<div>").addClass("bullet");
            if (m == k) { l.addClass("active") }
            g.append(l)
        }
    }

    function c() {
        $(".bullet.active").removeClass("active");
        var k = $(".slide.active").index() + 1;
        $(".bullet:nth-child(" + k + ")").addClass("active")
    }

    function d() {
        var n = $(".slide.active");
        var l = n.index();
        var m = (l + 1) % $(".bullet").size();
        var k = $(".slide:nth-child(" + (m + 1) + ")");
        i(n, k, l > m ? "inactiveRight" : "inactiveLeft");
        a = setTimeout(d, j)
    }

    function b() { if ($(".slide").length > 1) { a = setTimeout(d, j) } }
});